import React from "react";
import EmployeList from "./components/userlist";
import UniqueID from "react-html-id";

import {
  Container,
  Row,
  Col,
  Form,
  Button,
  Table,
  ButtonGroup,
  Modal
} from "react-bootstrap";


class App extends React.Component {
  constructor(props) {
    super(props);
    UniqueID.enableUniqueIds(this);
    this.state = {
      id: this.nextUniqueId(),
      Name: "",
      Property: "",
      Size: "",

      Description: "",
      modalShow: false,
      showeEmpId: 0,
      data: [
        {
          id: this.nextUniqueId(),
          Name: "shivraj",
          Property: "Flat",
          Size: "4bhk",

          Descripion: ""
        },
        {
          id: this.nextUniqueId(),
          Name: "shivraj",
          Property: "Plot",
          Size: "4x7",

          Descripion: ""
        }
      ]
    };
    this.initialstate = {
      id: this.nextUniqueId(),
          Name: "",
          Property: "",
          Size: "",

          Descripion: ""
    };
    this.HandleEvents = this.HandleEvents.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.deleEmployee = this.deleEmployee.bind(this);
    this.setModalShow = this.setModalShow.bind(this);
    this.setModalHide = this.setModalHide.bind(this);
  }
  setModalShow(id) {
    this.setState({
      modalShow: true,
      showeEmpId: id
    });
  }
  setModalHide() {
    this.setState({
      modalShow: false
    });
  }
  handleSubmit(event) {
    event.preventDefault();
    const dataList = {
      id: this.nextUniqueId(),
      Name: this.state.Name,
      Property: this.state.Property,
      Size: this.state.Size,

      employe: this.state.employe
    };
    this.setState({
      data: [...this.state.data, dataList]
    });
  }

  HandleEvents(event) {
    const name = event.target.name;
    const value = event.target.value;

    this.setState({
      [name]: value
    });
  }
  deleEmployee(index, e) {
    const emp = Object.assign([], this.state.data);
    emp.splice(index, 1);
    this.setState({
      data: emp
    });
  }
  render() {
    console.log(this.state.showeEmpId);
    const {showeEmpId} = this.state

    return (
      <div className="App" style={{ marginTop: "40px", marginBottom: "40px" }}>
        {this.state.data.length  ? (
        <Modal
          show={this.state.modalShow}
          onHide={this.setModalHide}
          size="lg"
          aria-labelledby="contained-modal-title-vcenter"
          centered
        >
          <Modal.Header closeButton>
            <Modal.Title id="contained-modal-title-vcenter">
            {this.state.data[showeEmpId].Name+' '+ this.state.data[showeEmpId].Property}
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Table striped bordered hover>
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Last Name</th>
                  <th>Size</th>

                  <th>employee</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td>{this.state.data[showeEmpId].Name}</td>
                  <td>{this.state.data[showeEmpId].Property}</td>
                  <td>{this.state.data[showeEmpId].Size}</td>

                  <td>{this.state.data[showeEmpId].employe}</td>
                </tr>
              </tbody>
            </Table>
          </Modal.Body>
        </Modal>
        ):('')
  }
        <Container>
          <Row>
            <Col lg={{ span: "8", offset: "2" }}>
              <EmployeList
                propertyData={this.state.data}
                deleEmployee={this.deleEmployee}
                viewEmployee={this.setModalShow}
              />
            </Col>
            <Col lg={{ span: "8", offset: "2" }}>
              <h3>Add Employee</h3>
              <br />
              <Form onSubmit={this.handleSubmit}>
                <Form.Group controlId="exampleForm.ControlInput1">
                  <Form.Label>First Name</Form.Label>
                  <Form.Control
                    value={this.state.Name}
                    type="text"
                    name="Name"
                    placeholder="First Name"
                    onChange={this.HandleEvents}
                  />
                </Form.Group>
                <Form.Group controlId="exampleForm.ControlInput2">
                  <Form.Label>Last Name</Form.Label>
                  <Form.Control
                    type="text"
                    placeholder="Last Name"
                    name="Property"
                    value={this.state.Property}
                    onChange={this.HandleEvents}
                  />
                </Form.Group>
                <Form.Group controlId="exampleForm.ControlInput2">
                  <Form.Label>Size</Form.Label>
                  <Form.Control
                    type="text"
                    placeholder="Email"
                    name="email"
                    value={this.state.Size}
                    onChange={this.HandleEvents}
                  />
                </Form.Group>

                <Form.Group controlId="exampleForm.ControlTextarea1">
                  <Form.Label>About Employee</Form.Label>
                  <Form.Control
                    as="textarea"
                    placeholder="About Employee"
                    rows="3"
                    name="employe"
                    value={this.state.employe}
                    onChange={this.HandleEvents}
                  />
                </Form.Group>
                <Button type="submit" >
                  Add Employee
                </Button>
              </Form>
            </Col>
          </Row>
        </Container>
      </div>
    );
  }
}
export default App;
